package code;

public class Arr4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[][] a = {{5,3},{7,8}};
		System.out.println("The Array Elements are: ");
		for(int i=0; i<2; i++) {
			for(int j=0; j<2; j++) {
				System.out.println(a[i][j] + "");
			}
		}
	}

}
